REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 0, 'Você se vamos preocupar sobre como nós matamos o monstro, Godfrey.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 1, 'Godfrey! Ouça-me, demônio! Ouça-me, para eu ser a Luz, aqui para entregar você de do mal!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 2, 'Bons. Eu tenho sua atenção, então, Godfrey?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 3, 'Nós podemos fazer este em um de dois modos, Godfrey. Primeiro, eu simplesmente perguntarei a você para dizer a mim o que o profano markings cauterizado nas páginas deste tomo quer dizer. O que diz você?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 4, 'Então ele é opção duas.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 5, 'Eu digo uma oração para você alma agora, perdida. Maio a Luz tomar você graciosamente.');
