REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 9, 'ADMITA A LUZ, Godfrey GOODARD!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 10, 'maio o poder de Leve compelir você!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 11, 'Eu pensei que você veria a Luz, Godfrey... Agora fale depressa, demônio. O que ele diz?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27316, 12, 'LIXO! Godfrey você me leva para um bobo? Não vomite sua propaganda de Açoite em um homem da Luz, pagão! Fale agora ou eu terei a arte de camponeses um banho da água benta e imergirei você em que gosta de um cachorro com pulgas!');
