REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 1, 'Um solitário orc contra o poder da Aliança???');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 2, 'Carregue!!!\n');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 3, '%s boqueadas para ar.');
