REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 5, 'O que nos... Lá, ao longe!\n');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 6, 'Uma Horda Zeppelin voa até a subida.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 7, 'Soldados, caiam em! Pareça com a Horda são para tomar outro atirado!\n');
