REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 9, 'Não force-me mão, orc. Nós não podemos deixar ye passar.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 10, 'Eu... Eu não posso fazer isto. Volte em yer navio e nós sobraremos yer vida. ');
