REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 13, 'Podia ser, Senhor Vadeando? Se Bolvar vive, mayhap existe esperança fer paz entre a Aliança e a Horda. Nós devemos alcançar o topo o este amaldiçoou lugar e livre o paladin!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 14, 'Prepare o Skybreaker fer uma antena assaltar na fortaleza!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37200, 15, 'Heróis, ye deve lutar yer caminho para um ponto de extração clara dentro de Icecrown. Nós tentaremos um encontro nas muralhas!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37217, 0, '%s clama com um alto, baying uivo!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37491, 1, 'Você bobos miserável nunca conseguiram selecionar uma asa de morcego decente.');
