REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27003, 0, 'Eu sei por que você veio - um daqueles Magnataur tolo nas planícies se intrometeu e conseguiu conseguir os arrasta envolvidos.  Você aprecia servir para eis gostarem de um cachorro?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27003, 1, 'Você busca seu líder... Pequena coisa, você empreende guerra contra os clãs de Grom thar o Thunderbringer ele mesmo.  Não seja tão ávido para apressar para sua morte.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27003, 2, 'Hah!  Assim seja.  Sopre o chifre de um magnataur líder no anel de tochas Sul do Dragonshrine Azul.  Faça paz com seus deuses... Grom thar virá.  ');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27482, 0, 'Obrigado, $r!');
