REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28041, 0, 'Assista suas costas. Estes Drakkari são um lote sórdido.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28041, 0, 'Estes Drakkari são notícias só ruins. Nós precisamos partir e encabeçar para Justiça Mantém!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28041, 0, 'Veja você ao redor.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28041, 0, 'Eu pergunto-me onde nós somos encabeçados. E quem vai lidar com estes sujeitos?');
