REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27658, 0, 'Obrigado por nos livrar, mortals.  Se precava, o vôo azul é alertado para sua presença.  Até agora, Malygos envia Varos Cloudstrider e seus guardiães de anel para defender o Oculus.  Você precisará de nossa ajuda para ter chance.');
