REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27756, 3, 'Agora que eu estou em meu poder cheio que eu posso apresentar Mártir.  Você pode usar ele para proteger outro drakes, mas eu tomarei muitos dano, então tenha certeza que você esteja usando Manobras Evasivas também!');
