REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (34490, 2, 'Conseguiu você bom!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (34489, 1, 'Queira ver o que eu posso fazer?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (34489, 2, 'Agora dança junta!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (27923, 0, 'Agarre-se para seu chapéu, $n! Para Scalawag nós vamos!');
