REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (13020, 0, 'Muito tarde, amigos! A corrupção do Nefarius tomou alça...Eu não posso...Controle eu mesmo.\n');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (13020, 1, 'Eu imploro você, mortals - FUJA! Fuja antes de eu perder toda sensação de controle! As iras de fogo preto dentro de meu coração! Eu DEVO- lanço isto! ');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (13020, 2, 'CHAMA! MORTE! DESTRUIÇÃO! Se agache, mortals antes da ira de Senhor...NÃO - eu DEVO lutar isto! Alexstrasza me ajuda, eu DEVO lutar isto! ');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (13020, 3, 'Ódio do Nefarius me fez mais forte que sempre! Você devia ter fugido enquanto você podia, mortals! A fúria de cursos de Blackrock por minhas veias! \n');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (13020, 4, 'Perdoe-me, $n! Sua morte só adiciona a meu fracasso! ');
