REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5391, 3, 'Obrigado, $n.  Eu lembrarei de você sempre.  Você pode achar minha caixa-forte em meu acampamento, norte de Stonard.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5391, 4, '%s sussurros para $n o secreto a abrir sua caixa-forte.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5391, 5, '%s desaparece no pântano.');
