REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 1, 'O céu lamenta na devastação gloriosa destas terras! Lágrimas logo, Fútil do Azeroth choverá abaixo em nós!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 2, 'Cavaleiros da morte de Acherus, a marcha da morte começa!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 3, 'Soldados do Açoite, cavaleiros da morte de Acherus, minions da escuridão: Ouça o telefonema do Highlord!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 4, 'SUBA!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 5, 'Os esquis viram vermelho com o sangue do caído! O Rei de Lich vigia nós, minions! Saia só cinzas e miséria em sua destrutiva despertam!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29176, 6, 'Exércitos de açoite abordam!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29178, 7, 'Esteja rápido, irmãos e irmãs! A Luz prevalecerá!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Ajoelhe antes do Highlord!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Você não permanece nenhuma chance!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'O Açoite destruirá este lugar!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Sua vida é multa.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Vida é sem sentida sem sofrer!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'quanta mais longas suas forças resistirão?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'O Argent Amanhece está acabado!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Sobressalente ninguém!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'O que é isto?! Meu... Eu não posso atingir...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'OBEDEÇA-ME, LÂMINA!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Você fará como eu comando! Eu estou em controle aqui!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Eu... Eu não posso... A lâmina me luta...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'O que estar acontecendo para mim?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Poder... Mingúe...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Ashbringer me desafia...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 8, 'Minions, venha para minha ajuda!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29175, 24, 'Você não pode ganhar, Darion!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29175, 25, 'Traga eles antes da capela!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 26, 'Permaneça abaixo, cavaleiros da morte. Nós perdemos. A Luz... Este lugar... Nenhuma esperança...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29175, 27, 'Você não aprendeu nada, menino? Você se tornou tudo que seu pai lutado contra! Assim covarde, Arthas, você permitiu você mesmo ser consumido pela escuridão, o ódio... Alimentando na miséria daquela você torturou e matou!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29175, 28, 'Seu mestre sabe o que está em baixo da capela. É por isso que ele não ousa show seu rosto! Ele é mandado você e seus cavaleiros da morte para encontrar sua destruição, Darion.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29175, 29, 'O que você está sentindo agora mesmo é a angústia de mil perdeu almas! Almas que você e seu mestre trazidos aqui! A Luz despedaçar você, Darion!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 30, 'Salve sua respiração, homem velho. Poderia ser o último você já desenha.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29227, 31, 'Meu filho! Meu menino querido, bonito!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 32, 'Pai!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29173, 33, 'Argh... O que... É...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29228, 34, 'Pai, você retornou!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (29228, 35, 'Você foi ido muito tempo, Pai. Eu pensei...');
