REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15608, 6, 'Eu sou agradecido para sua ajuda, campeões. Agora, Horda do Gul dan varrerá através deste mundo gosta de um enxame de gafanhoto, e todos os meus projetos, todos meus cuidadosamente-deitados planos legar afinal cair em lugar.');
