REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (37955, 12, '%s entra em uma ira mais frenética!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (36853, 11, '%s entra em uma ira mais frenética!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15956, 0, 'Shhh... Isso tudo será acima de logotipo.');
