REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15956, 0, 'Sim, corra! Faz a bomba de sangue mais rápido!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15956, 1, 'Ahh... Bem-vinda a minha sala de estar.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15956, 1, 'Eu ouço pequena batida de corações. Sim... Batida mais rápida agora... Logo a batida parará.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15956, 1, 'Onde ir? O que para Tantas escolhas que todos terminar em dor, termine na morte.');
