REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15956, 1, 'Mais íntimos agora. Gostoso morsels. Eu tenho estado muito tempo sem comida, sem sangue para beber.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15956, 2, 'Shhh... Isso tudo será acima de logo.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15953, 0, '%s entra em um frenesi!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15953, 1, 'Mate eles no nome do mestre!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15953, 1, 'Você não pode esconder de mim!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15953, 1, 'Ajoelhe antes de mim, lombriga!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15953, 1, 'Corra enquanto você ainda pode!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15953, 2, 'Você falhou!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15953, 2, 'Miserável patético!');
