REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16064, 0, 'Que desperdício sangrento isto é!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16064, 5, 'Para braços, ye roustabouts! Nós temos companhia!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16064, 6, 'Eu ouvi sobre suficiente um  yer snivelin ! Fechado yer flytrap antes de eu fechar isto para ye!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16064, 1, 'Eu sou ir aprecio matança estes frouxos-jawed narcisos!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16064, 2, 'Eu gosto de minha carne extra quebradiça!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16064, 3, 'Da próxima vez, traga mais amigos!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16064, 4, 'Que desperdício sangrento isto é!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16065, 0, 'Touche...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16065, 5, 'Venha, Zeliek, não dirija eles. Não antes de nós ter nossa diversão!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16065, 6, 'Eu espero que eles fiquem vivo longo suficiente para mim para... Apresente eu mesmo.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16065, 1, 'A primeira matança vai para mim! Qualquer um cuidado para apostar?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16065, 2, 'Sua vida é minha!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16065, 3, 'Quem é próximo?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16065, 4, 'Touche...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 0, 'Morte... Não parará-me.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 0, 'Nenhum deve passar!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 0, 'Esteja quieto!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 5, 'Suficiente palrando. Deixe eles vir. Nós devemos moer seus ossos para espanar.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 6, 'Conserve sua raiva. Arreie sua ira. Vocês todos terão saídas para suas frustrações logo o bastante.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 1, 'Vida é sem sentida. Está na morte que nós estamos verdadeiramente testados.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 2, 'Curve para o poder do Açoite!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 3, 'Você não achará nenhuma paz na morte.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 3, 'O mestre é será feito.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (30549, 4, 'Morte... Não parará-me.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16063, 0, 'É... Como devia ser. ');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16063, 5, 'Invasores, cessem esta aventura tola de uma vez! Se vire enquanto você ainda pode!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16063, 6, 'Talvez eles virão para seus sentidos... E vá embora tão rápido quanto eles podem.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16063, 1, 'Não continue! Volte enquanto existe ainda tempo!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16063, 2, 'Eu não tenho nenhuma escolha mas obedecer!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16063, 3, 'Perdoe-me!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16063, 4, 'É... Como devia ser. ');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16060, 5, 'Eu... Seja... Desfeito.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16060, 2, 'Você lutou duro de invadir o reino da ceifeira.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16060, 3, 'Agora existe só uma maneira fora- caminhar para o caminho só do maldito.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16060, 4, 'Eu esperei por muito tempo suficiente. Agora você enfrenta a ceifeira de almas.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15936, 0, '%s pressas para atacar mais uma vez!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15936, 0, 'Eu vejo você!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15936, 0, 'Você... São próximos.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15936, 1, 'Fim seus olhos. Durma.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15936, 2, 'As corridas do mundo perecerão. É só um assunto de tempo.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15936, 2, 'Eu vejo sofrimento infinito. Eu vejo tormento. Eu vejo ira. Eu vejo tudo...');
