REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (21027, 1, 'Atacantes de Naga! Defenda você mesmo!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (21027, 2, 'Grant mim proteção, $n. Eu devo atravessar sua magia suja!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (21027, 3, 'O naga de Coilskar são excepcionalmente cruéis para seus prisioneiros. É um milagre que eu sobrevivi dentro daquela prisão aguada para desde que eu fiz. Earthmother é louvado.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (21027, 3, 'Senhora Vashj deve responder para estas atrocidades. Ela deve ser trazida para justiça!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (21027, 3, 'A natureza tumultuosos dos grandes vias fluviais de Azeroth e Draenor são um resultado direto de álcool da água atormentado.');
