REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22834, 3, 'Você -- você não é um deles! Vamos sair de visão antes deles nos achar! Se apresse!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22834, 4, 'Eles são seguinte me, $r! Depressa, diga a mim por que você está aqui antes de nós ser descobertos! Se eles pegarem até mim, quererá dizer minha morte e seu!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (17307, 0, 'Você enfrentou muitos desafios.... Um piedade eis eram todos em vão! Logotipo suas pessoas ajoelharão para MEU senhor!');
