REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15341, 1, 'Não mais nós esperarão atrás de portas e paredes trancadas de pedra! Não mais lega nossa vingança ser negada! Os dragões eles mesmos tremerão antes de nossa ira!\n');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15341, 2, 'Medo é para o inimigo! Medo e morte!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15341, 3, 'Staghelm choramingará e implorará por sua vida, da mesma maneira que seu de um filho fez! Mil anos de injustiça terminarão este dia!\n');
