REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (17073, 0, 'Devia ter me sido!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (17073, 0, 'Esteja em paz, minha doce, doce, Luhanaa.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (17073, 0, 'Eu nunca esquecerei você, meu amor.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (17073, 0, 'Se eu já achar aqueles responsável pelo impacto...!');
