REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16589, 0, 'Então nós estávamos aí - mim e o batalhão, fazendo um rotineira recon osso abaixo estrada. Nós enviamos o explorador em cima adiante para procurar nenhuma caso de que qualquer coisa perigoso onda.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16589, 1, 'Certo suficiente, explorador veio para runnin atrás, conversando turno um pouco de orcs caminho abaixo. Eles não nos viram ainda assim nós figuramos que nós iríamos ser familiarizados.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16589, 2, 'Ventosas vermelhas grandes! Maior que qualquer orc eu já veria. Músculos fora para aqui, dentes de elefante fora para aqui... ');
