REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16589, 4, 'Fel orcs...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16589, 5, 'Eu dunno, grunha. Eu somente - não faça - saiba...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16589, 6, 'Mas isto não é o pior disto. Antes de eu poder nos puxar de volta, um grupo deles manchado nós e atacados.');
