REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 3, 'Vá embora softknuckle! Goregek vai conseguir você!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 3, 'Vá embora mudo softknuckle! Nós vamos cutucar você mais!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 3, 'Hardknuckles ainda caminha nas mãos... Pareça tipo de mudo para Goregek.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 3, 'Coisa de escravo cuidadoso, o grande ruim softnuckles poderia comer você! Hah!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 2, 'Você só consegue você mesmo atacar! Hah! Você tão mudo, moureje coisa.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 2, 'Lance era fraco de qualquer maneira... Por que outro que você pensa que Goregek não aborrecer com ele?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 2, 'Mais velhos talvez diga outro material, mas você sempre é mourejar coisa para Goregek.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 2, 'Por que Goregek até traz você. Seu inútil.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28214, 1, 'Aquele como ele feito, moureje coisa. Talvez você compreender como em algum dia.');
