REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28122, 5, 'Esta briga é todo seu!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28122, 6, 'É um conforto pequeno para ver que eles não trouxeram qualquer de volta em undeath.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28122, 4, 'Onde eles estão arrastando aqueles corpos? O que eles estão fazendo para eles?');
