REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28122, 3, 'Aqui eu sou, cérebro o tamanho de um planeta, e eles me pedem para tomar você para a outra aldeia. Chame aquela satisfação de trabalho? Porque eu não faço.');
