REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28121, 5, 'Tartek nenhuma partida para você! Você conseguiu as habilidades. Sim!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28121, 4, 'Tempo para esconder homens de filhote de cachorro! Nós vindo por você... Melhor entrar suas coisas de colina!');
