REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28215, 9, 'Coisa morta lá já aprende lição de pilar quebrada. Deixe esta lição para coisas mudas, mortas. Indo para casa agora.');
