REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 0, 'Harkek Frenzyheart Mais velho satisfatório. Ele me ajuda muito quando eu era menor e não gostando de Dajik.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 0, 'Eu não em uma grande pressa. Nós podemos descansar aqui um enquanto se queremos.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 0, 'Eu não vivo aqui sempre... Eu viajei lote do mundo sozinho longo atrás. que eu gosto de todas as histórias de bestas e dragões e deuses e material.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 0, 'Você devia ir deixar galinhas fora novamente e diz que Goregek fez isto. Nunca fique velho.');
