REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 2, 'Dajik esquece o que ele fazendo, onde ele em... Algum dia até esquece seu próprio nome. Faça ele não tão bom professor às vezes.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 2, 'Harkek Frenzyheart Mais velho satisfatório. Ele me ajuda muito quando eu era menor e não gostando de Dajik.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 2, 'Goregek nunca \"stumble\" em cabana de galinha... Goregek sempre tem que alimentar sua barriga muda. Sempre uma galinha faltando quando nós pegarmos eles todos.');
