REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 3, 'Coma isto grandes-línguas!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 3, 'Hah! Deixe algum para Zepik também.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 3, 'Aquele lago aqui perto realmente cheio de coisas do mar. Não certo como isso tudo chega aqui... Talvez em inundações?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 3, 'Eu penso uma razão que eu gosto de caçar grandes-línguas tantas... Eles não tão mudos quanto as outras caças. Eles dão um real lutar.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 3, 'Névoa grande-línguas são espertos com sua coisa de santuário às vezes... A área de cobertura inteira com névoa e ficam duros de achar algum.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 3, 'Hah! Esta a parte divertida!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28216, 3, 'Chama! Morte! Destruição! As iras de fogo preto dentro de meu coração! Eu devo... Lance isto! Hahah!');
