REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (25729, 2, 'Eu... Eu não posso acreditar nisto... Saurfang... I... Eu sou honrado... Honrado ter lutado junto apóia você, senhor.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (25729, 3, 'Você devia retornar a Warsong Segura agora, amigo. Hellscream seguramente quererá ouvir do que acabou de acontecer aqui... De Saurfang...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (25618, 0, 'É este isto? Isto tudo é a Horda poderosa podia reunir?');
