REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (31135, 9, 'Sigrid Iceborn retornou às alturas de Jotunheim se provar contra $n.  Quando últimos eles se encontraram, $n bested ela em combate pessoal.  Vamos ver o resultado desta partida.');
