REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (31135, 13, 'Dos intestinos do Underhalls vem para Carnagem.  Valente e tolo $n aceitou o desafio.  $G Ele : Ela; E $g seu : ela; O grupo está pronto para enfrentar a monstruosidade.');
