REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5734, 0, 'Hmm, pareceria Keever precisa de um novo assunto. Se aquele bobo Abernathy mantém assuntos do tomada Keever, Keever pode ter que ter uma palavra com ele.');
