REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5734, 4, 'Não que Keever estava pulando. Keever pode ter adicionado demais earthroot. Vamos ver se o segundo soro fará o que Keever precisa.');
