REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5734, 6, '%s cutuca o esquilo penugento pequeno com decepção óbvia.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5734, 7, 'Bem, isto só não é direito. A criatura é extremamente pequena. Vamos ver o que terceiro grupo do Keever fará.');
