REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5734, 9, '%s cutuca o skittish coelho.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5734, 10, 'Keever é infeliz com este. Talvez se Keever fosse tentar uma dose maior, isso pode consertar este dilema.');
