REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5734, 12, 'O que é isto? Keever pediu uma ovelha? Keever quis uma arma de grande poder e tudo que ele é era esta ovelha. Keever está muito desapontado.');
