REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (24703, 2, 'Eu não sacrificarei meus guerreiros para noções românticas da morte gloriosa no campo de batalha. Que uso está lutando por sobrevivência se ninguém sobreviver?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (24703, 3, 'Existirá um tempo para a batalha, Durm. Mas primeiro, nós devemos achar um caminho para até as chances. O que você teria-me fazer, Aeire?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (24709, 0, 'Eu tomarei aqueles. Nós não podemos ter o comandante poderoso agachando em medo de alguns esconde.');
