REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (24703, 4, 'O que é sua honra no valor de, Durm? Sua vida? Meu? A vida de cega Greatfather Mahan velho, quem levantou você de infância?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (24703, 5, 'Honra é sem sentida para um homem morto. Um guerreiro pode lançar ele mesmo na batalha para causa da honra, mas um comandante é responsável por muito mais. Nós seguiremos plano do Sábio Aeire e continuaremos a defender nossa aldeia.');
