REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28175, 1, 'Nunca ouvidos falar disto. Pareça com mau puro.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28175, 2, 'Vamos ver como levanta para o poder de meu machado!');
