REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (28244, 6, 'Claramente isto está nos conseguindo em nenhuma parte. Nós precisamos conseguir este saronite para Highlord Que vadeia para estudo adicional.');
