REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (26666, 1, 'Lembre do dwarven diário que era descoberto? Bem, isto anão podia ser exatamente o que nós precisamos perseguir Brann Bronzebeard e o capturar.');
