REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (35361, 1, 'Eu espero que você verá o mérito destes eventos a tempo, Garrosh. Nós não podemos ganhar contra o Açoite se nós continuarmos guerrear contra um ao outro.');
