REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15362, 1, 'Seja firme, campeão. Eu sei por que é que você está aqui e eu sei o que é que você busca. Eranikus não desistirá do fragmento livremente. Ele foi torcido... Trançada pela mesma força que você busca destruir.');
