REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15362, 3, 'Entenda isto, Eranikus quer nada além de ser trazido para Azeroth do Sonho. Uma vez que ele está fora, ele parará em nada destruir minha manifestação física. Esta, porém, é a única entrada que você podia recuperar o fragmento de cetro.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (15362, 4, 'Você o devolverá neste mundo, campeão.');
