REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'Meus olhos! Queima... queima...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'Tortura mim não mais! Lance-me, mortal! O Pântano de Duelos... Mortais... Por favor...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'Tolos mortais, eu rasgarei sua alma em duas uma vez que eu sou lançado!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'Você é um agente de seu deus mau, bobo. Eu verei que você destruiu!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'Minha alma não é umas quinquilharias! Mortais, você deve me lançar destas cadeias!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'Machuca tanto... Itharius, meu amigo velho... Por favor me ajude...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'O pesadelo já concluirá?! Eu não posso forçar eu mesmo despertar!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (8506, 0, 'Minha carga para assistir o templo falhou... Minha alma corrompida não sabe nenhuma paz...');
